/** * https://leetcode-cn.com/problems/middle-of-the-linked-list/ * 876. 链表的中间结点 * 快慢指针,一个走两步,一个走一步. * 当快指针没有下一个节点时,说明当前链表节点为单数. * 当快指针有下一个节点时,说明当前节点为双数 * * @author iocntop@gmail.com */package com.sukdao.leetcode;import com.sukdao.leetcode.entity.ListNode;import com.sukdao.leetcode.util.ListNodeUtils;public class MiddleOfTheLinkedList {    public ListNode middleNode(ListNode head) {        ListNode slow = head;        ListNode fast = head;        while (fast.next != null && fast.next.next != null) {            fast = fast.next.next;            slow = slow.next;        }        return fast.next != null ? slow.next : slow;    }    public static void main(String[] args){       ListNode node =  ListNodeUtils.generatorOrderly();        ListNodeUtils.print(node);        System.out.println("---------------------");        MiddleOfTheLinkedList middleNode = new MiddleOfTheLinkedList();        ListNode midd = middleNode.middleNode(node);        ListNodeUtils.print(midd);    }}